ight\n}\n\n/* define a method for shape */\nfunc getArea(shape Shape) float64 {\n   return shape.area()\n}\n\nfunc main() {\n   circle := Circle{x:0,y:0,radius:5}\n   rectangle := Rectangle {width:10, height:5}\n   \n   fmt.Printf(\"Circle area: %f\\n\",getArea(circle))\n   fmt.Printf(\"Rectangle area: %f\\n\",getArea(rectangle))\n}\n\', \'\nCircle area: 78.539816\nRectangle area: 50.000000\n\']','[\'\n/* define an interface */\ntype interface_name interface {\n   method_name1 [return_type]\n   method_name2 [return_type]\n   method_name3 [return_type]\n   ...\n   method_namen [return_type]\n}\n\n/* define a struct */\ntype struct_name struct {\n   /* variables */\n}\n\n/* implement interface methods*/\nfunc (struct_name_variable struct_name) method_name1() [return_type] {\n   /* method implementation */\n}\n...\nfunc (struct_name_variable struct_name) method_namen() [return_type] {\n   /* method implementation */\n}\n\', \'\nCircle area: 78.539816\nRectangle area: 50.000000\n\']','[]','[]'),(23,'[\'Go - Error Handling\']','[\'Example\', \'Useful Video Courses\']','[]','[\'https://www.tutorialspoint.com/go/go_interfaces.htm\', \'https://www.tutorialspoint.com/go/go_questions_answers.htm\', \'http://tpcg.io/pt5b4w\', \'https://www.tutorialspoint.com/inverse_trigonometric_function/index.asp\', \'https://www.tutorialspoint.com/inverse_trigonometric_function/index.asp\', \'https://www.tutorialspoint.com/profile/Ridhi-Arora\', \'https://www.tutorialspoint.com/inverse_trigonometric_function/index.asp\', \'https://www.tutorialspoint.com/google_plus_online_training/index.asp\', \'https://www.tutorialspoint.com/google_plus_online_training/index.asp\', \'https://www.tutorialspoint.com/profile/Asif-Hussain\', \'https://www.tutorialspoint.com/google_plus_online_training/index.asp\', \'https://www.tutorialspoint.com/oracle_goldengate_online_training/index.asp\', \'https://www.tutorialspoint.com/oracle_goldengate_online_training/index.asp\', \'https://www.tutorialspoint.com/profil