sing SAX API.\']','[\'\n<?xml version = \"1.0\" encoding = \"utf-8\"?>\n<tutors>\n   <course>\n      <name>Android</name>\n      <country>India</country>\n      <email>contact@tutorialspoint.com</email>\n      <phone>123456789</phone>\n   </course>\n   \n   <course>\n      <name>Java</name>\n      <country>India</country>\n      <email>contact@tutorialspoint.com</email>\n      <phone>123456789</phone>\n   </course>\n   \n   <course>\n      <name>HTML</name>\n      <country>India</country>\n      <email>contact@tutorialspoint.com</email>\n      <phone>123456789</phone>\n   </course>\n</tutors>\n\', \'\n<?php\n   //Reading XML using the SAX(Simple API for XML) parser \n   \n   $tutors   = array();\n   $elements   = null;\n   \n   // Called to this function when tags are opened \n   function startElements($parser, $name, $attrs) {\n      global $tutors, $elements;\n      \n      if(!empty($name)) {\n         if ($name == \'COURSE\') {\n            // creating an array to store information\n            $tutors []= array();\n         }\n         $elements = $name;\n      }\n   }\n   \n   // Called to this function when tags are closed \n   function endElements($parser, $name) {\n      global $elements;\n      \n      if(!empty($name)) {\n         $elements = null;\n      }\n   }\n   \n   // Called on the text between the start and end of the tags\n   function characterData($parser, $data) {\n      global $tutors, $elements;\n      \n      if(!empty($data)) {\n         if ($elements == \'NAME\' || $elements == \'COUNTRY\' ||  $elements == \'EMAIL\' ||  $elements == \'PHONE\') {\n            $tutors[count($tutors)-1][$elements] = trim($data);\n         }\n      }\n   }\n   \n   // Creates a new XML parser and returns a resource handle referencing it to be used by the other XML functions. \n   $parser = xml_parser_create(); \n   \n   xml_set_element_handler($parser, \"startElements\", \"endElements\");\n   xml_set_character_data_handler($parser, \"characterData\");\n   \n   // open xml file\n   if (!($handle = fopen(\'sax.xml\', \"r\"))) {\n      die(\"could not open XML input\");\n   }\n   \n   while($data = fread($handle, 4096)) // read xml file {\n      xml_parse($parser, $data);  // start parsing an xml document \n   }\n   \n   xml_parser_free($parser); // deletes the parser\n   $i = 1;\n   \n   foreach($tutors as $course) {\n      echo \"course No - \".$i.\'<br/>\';\n      echo \"course Name - \".$course[\'NAME\'].\'<br/>\';\n      echo \"Country - \".$course[\'COUNTRY\'].\'<br/>\';\n      echo \"Email - \".$course[\'EMAIL\'].\'<br/>\';\n      echo \"Phone - \".$course[\'PHONE\'].\'<hr/>\'; \n      $i++; \n   }\n?>\n\']','[]','[]','[]'),(47,'[\'PHP - DOM Parser Example\']','[\'Useful Video Courses\']','[]','[\'https://www.tutorialspoint.com/php/php_sax_parser_example.htm\', \'https://www.tutorialspoint.com/php/php_frame_works.htm\', \'https://www.tutorialspoint.com/php_online_training/index.asp\', \'https://www.tutorialspoint.com/php_online_training/index.asp\', \'https://www.tutorialspoint.com/profile/malhar_lathkar\', \'https://www.tutorialspoint.com/php_online_training/index.asp\', \'https://www.tutorialspoint.com/become_php_facebook_developer_password_less_authentication/index.asp\', \'https://www.tutorialspoint.com/become_php_facebook_developer_password_less_authentication/index.asp\', \'https://www.tutorialspoint.com/profile/syed_raza\', \'https://www.tutorialspoint.com/become_php_facebook_developer_password_less_authentication/index.asp\', \'https://www.tutorialspoint.com/ultimate_php_and_mysql_web_development_course_and_oop_coding/index.asp\', \'https://www.tutorialspoint.com/ultimate_php_and_mysql_web_development_course_and_oop_coding/index.asp\', \'https://www.tutorialspoint.com/profile/frahaan_hussain\', \'https://www.tutorialspoint.com/ultimate_php_and_mysql_web_development_course_and_oop_coding/index.asp\', \'https://www.tutorialspoint.com/learn_php_from_scratch/index.asp\', \'https://www.tutorialspoint.com/learn_php